INSERT INTO letter_templates (letter_name, letter_title, letter_content, enabled) VALUES
('Evaluation:Promotion', 'Promotion', '<h3 style="text-align: center;">&nbsp;</h3><h3 style="text-align: center;">&nbsp;</h3><h3 style="text-align: center;"><u><strong>Promotion Letter</strong></u></h3><p style="text-align:right">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {{date}}</p><p><strong>{{employee_full_name}},&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<br />{{old_designation}},&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;<br />{{company_name}},&nbsp;&nbsp; &nbsp;<br />{{company_area}}.</p><p>Subject: Promotion as <strong>{{designation}}</strong></p><p>Dear {{employee_name}},</p><p style="text-align:justify">We would like to express our appreciation and commendation for all the passion and commitment you have been exhibiting in your existing role.</p><p style="text-align:justify">&nbsp;</p><p style="text-align:justify">In recognition of your contribution, it is our pleasure to inform you that you have been promoted as<strong> &quot;{{designation}}&quot;</strong> for<strong> </strong>{{company_name}}<strong> </strong>w.e.f&nbsp; {{effects_from}}.</p><p style="text-align:justify">&nbsp;</p><p>We hope this promotion will encourage and motivate you to continue putting in your best and perform well on all projects being led by you.</p><p style="text-align:justify">Congratulations and keep the good work.&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style="text-align:justify">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style="text-align:justify">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>For {{company_name}}&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p>&nbsp;</p><p>&nbsp;General Manager - Operations&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>', 1), 
('Evaluation:Increment', 'Increment', '<p>&nbsp;</p><p style="text-align:right">&nbsp;<br />&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><h3 style="text-align:center">&nbsp;&nbsp; <u><strong>Increment Letter</strong></u></h3><p style="text-align:right">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {{date}}</p><p><strong>{{employee_full_name}},</strong><br />{{designation}},<br />{{company_name}},<br />{{company_area}}.</p><p>Dear {{employee_name}},</p><p style="text-align:justify">Consequent to the review of your performance during the period of {{employee_doj}} to {{effects_from}}, we have pleasure in informing you that, your salary is being revised from Rs. {{old_salary}} ({{old_salary_words}}) to Rs. {{new_salary}} ({{new_salary_words}}) w.e.f {{effects_from}}.</p><p style="text-align:justify">&nbsp;</p><p style="text-align:justify">The other terms and conditions of your appointment remains unchanged.</p><p style="text-align:justify">&nbsp;</p><p style="text-align:justify">We look forward to your valuable contributions and wish you all the very best for a rewarding career with the organization.</p><p style="text-align:justify">&nbsp;</p><p style="text-align:justify">Congratulations and keep the good work</p><p style="text-align:justify">&nbsp;</p><p>For {{company_name}}</p><p>&nbsp;</p><p>General Manager - Operations</p>', 1), 
('Evaluation:Promotion come Increment', 'Promotion come Increment', '<p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;Dear <strong>{{employee_name}}</strong>,</p><p style="text-align: justify;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; In appreciation of your services, the Management is pleased to designate you as <strong>{{designation}}</strong> and enhance your existing monthly salary by <strong>{{increment_percentage}}</strong> i.e., from <strong>{{old_salary}}</strong> to <strong>{{new_salary}}</strong> with effect from <strong>{{effects_from}}</strong></p><p style="text-align: justify;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;We hope better performance from you in coming years. Wish you best of luck</p>', 1), 
('Evaluation:Transfer', 'Transfer', '<p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;Dear <strong>{{employee_name}}</strong>,</p><p style="text-align: justify;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; It is our pleasure to inform you that you have been transferred to our <strong>{{branch_name}}</strong> branch based on your performance. The company has been assessing your progress and you have been a valuable asset to the company.</p><p style="text-align: justify;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; You will be joining our <strong>{{branch_name}}</strong> branch on <strong>{{doj}}</strong>. You will be given 15 days break to join the company. You can contact the HR department to know details about your salary increase.</p><p style="text-align: justify;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Wish you all the best!</p>', 1), 
('Offer:Confirmation', 'Confirmation', '<p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;</p><p>Dear <strong>{{employee_name}}</strong>,</p><p style="text-align: justify;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; We have the pleasure in informing you that, your services are being confirmed as <strong>{{designation}}</strong> with effect from <strong>{{confirmation_date}}</strong>.</p><p style="text-align: justify;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; All the other terms and conditions as detailed in your appointment letter remain unchanged. We look forward to your valuable contributions and wish you all the very best for a rewarding career with the organisation. Please sign the duplicate copy of this letter as a token of acceptance of the same.</p>', 1), 
('Welcome:Letter', 'Welcome Letter', '<p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">&nbsp;</p><p style="text-align: justify;">Hi <strong>{{employee_name}}</strong>,<br /><br />We are delighted you have joined us! Your contribution is important to ensure our sustained success and growth. We hope that your career here will be a gratifying one. You would get maximum support from the whole of our team and we look forward to having the best relations with you.&nbsp;<br /><br />I welcome to u on the behalf of {{company_name}}<br />I hope you will find {{company_name}} as a cool place to work with!!!&nbsp;<br /><br />Please let me know in case of any problem.&nbsp;<br /><br />Thanks and Regards<br />Team HR</p>', 1), 
('Joining:Letter', 'Joining Letter', '<h4 style="text-align: center;">&nbsp;</h4><h4 style="text-align: center;">&nbsp;</h4><h4 style="text-align: center;">&nbsp;</h4><h3 style="text-align: center;">&nbsp;<strong> Joining Letter</strong></h3><p style="text-align: right;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Date: {{date}}</p><p>To<br />The HR Manager,<br />{{company_name}}<br />{{company_area}}.</p><p>Sub: Joining Letter</p><p>Dear Sir,</p><p style="text-align: justify;">I am pleased to accept your offer and I have honor to inform you that I am joining with {{company_name}} as {{designation}} effective from today {{employee_doj}} in respect to your offer letter. I understand and accept the terms and conditions of employment that you have mentioned in the offer letter.</p><p>Yours Sincerely,</p><p>Employee Signature with date:</p><p>{{employee_name}}<br />Employee Name</p><p>&nbsp;</p>', 1), 
('Relieving Letter', 'Relieving Letter', '<p style="text-align: right;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p><p style="text-align: right;">&nbsp;</p><p style="text-align: right;">&nbsp;</p><p style="text-align: right;">&nbsp;&nbsp; Date: {{date}}</p><p>Sub: Relieving Letter</p><p style="text-align:center"><strong>TO WHOMSOEVER IT MAY CONCERN</strong></p><p>Dear {{employee_name}},</p><p style="text-align: justify;">&nbsp;&nbsp; This is reference to your letter of resignation on {{last_working_date}}, wherein you have requested to be relieved from the services of the company effective from {{last_working_date}}.</p><p style="text-align: justify;">&nbsp;&nbsp;&nbsp; We would like to inform you that your resignation is hereby accepted and you are being relieved from the services of the company with effect from closing office hours of {{last_working_date}}.</p><p style="text-align: justify;">&nbsp;</p><p><strong>For {{company_name}}</strong></p><p>&nbsp;</p><p><strong>Executive - Human Resources</strong></p><p><br /><strong><!--[if !supportLineBreakNewLine]--></strong></p>', 1), 
('Offer:Letter', 'Offer Letter', '<p>&nbsp;</p><p>&nbsp;</p><h3 style="text-align: center;">&nbsp;</h3><h3 style="text-align: center;"><u><strong>Offer Letter</strong></u></h3><p>&nbsp; Dear <strong>{{employee_name}}</strong>,</p><p style="text-align: justify;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; It is my pleasure to extend the following offer of employment to you on behalf of <strong>{{company_name}}</strong>. You are expected to join duty on <strong>{{employee_doj}}</strong>.</p><p style="text-align: justify;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; You are appointed to the position of <strong>{{designation}}</strong>. As <strong>{{designation}}</strong>, your starting monthly remuneration will be<strong> Rs. {{salary}} </strong>({{new_salary_words}}). You will be on a probation period of <strong>{{days}} </strong>days.</p><p style="text-align: justify;">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; We welcome you as part of our team. Congratulations and wish you best of luck.</p>', 1), 
('Termination Letter', 'Termination Letter', '<p style="text-align: right;">&nbsp;</p><p style="text-align: right;">&nbsp;</p><p style="text-align: right;">&nbsp;</p><p style="text-align: right;">{{date}}</p><p>{{employee_id}}</p><p>To<br />{{employee_full_name}}</p><p style="text-align: center;"><strong>Sub: Termination </strong><strong>from</strong><strong> Service</strong></p><p style="text-align: justify;">Dear {{employee_name}},<br />This is with regards to your absconding of work, you didn''t report to office from {{last_working_date}} onwards. We have not received any information from your side regarding your absence either telephonically or personally. Considering this fact which proves beyond doubt that you are no more interested in performing your duties and responsibilities. Taking in view of the above facts and circumstances we have decided to terminate your services with effective from {{date}} onwards.</p><p>Yours Sincerely,</p><p>&nbsp;</p><p>&nbsp;</p><p>&nbsp;Manager - Corporate HR</p><p>&nbsp;</p><p>&nbsp;</p>', 1),
('Experience Letter', 'Experience Letter', '<p><strong>&nbsp;</strong></p><p style="text-align: center;">&nbsp;</p><p style="text-align: center;">&nbsp;</p><p style="text-align: center;"><strong>&nbsp;</strong><u><strong>&nbsp; TO WHOMSOVER IT MAY CONCERN</strong></u></p><p style="text-align: justify;">This is to certify that {{employee_name}} has worked with us as {{designation_name}} from {{employee_doj}} to {{last_working_date}}.</p><p style="text-align: justify;">She/He has resigned and left the organization of her own accord.<br />&nbsp;</p><p><strong>For {{company_name}}.</strong></p><p>&nbsp;</p><p>&nbsp;</p><p><br /><!--[if !supportLineBreakNewLine]--><br /><!--[endif]--></p><p>&nbsp;</p>', 1);
